import styled from "styled-components";

import data from "../../data.json";
const backgroundColor = data.map((i) => i.label);
console.log(backgroundColor);

switch (backgroundColor) {
  case ".docx":
    console.log(".docx");
    let color = "background-color:#c12f2f";
    break;
  case ".pdf":
    console.log(".pdf");
    // "#c12f2f";
    break;
  case ".mp3":
    console.log(".mp3");
    // "#c12f2f";
    break;
  case ".psd":
    console.log(".psd");
    // "#c12f2f";
    break;
  case ".mp4":
    console.log(".mp4");
    // "#c12f2f";
    break;

  default:
    break;
}
// switch (backgroundColor);
export const StatsCardStyled = styled.div`
  width: 300px;
  /* height: 500px; */
  /* padding: 10px; */
  padding-top: 20px;
  padding-bottom: 20px;
  margin-top: 20px;
  margin-left: auto;
  margin-right: auto;
  border: 2px solid white;

  border-radius: 20px;
  box-shadow: 0px -5px 10px white;
`;

export const TitleStyled = styled.h2`
  text-align: center;
  font-size: 20px;
`;

export const ListStyled = styled.ul`
  display: flex;
  /* flex-grow: 1; */

  margin-top: 20px;
  /* ${backgroundColor}; */
`;

export const ItemStyled = styled.li`
  flex-grow: 1;
  text-align: center;
`;
export const LadelStyled = styled.p``;
export const PercentageStyled = styled.p``;
