  // });

  return (
    <StatsCardStyled>
      {title && <TitleStyled>{title.toUpperCase()}</TitleStyled>}
      <ListStyled>
        {statistics.map((i) => {
          console.log(i.label);

          return (
            <ItemStyled key={i.id} color={i.label}>
              <p>{i.label}</p>
              <p>{i.percentage}%</p>
            </ItemStyled>
          );
        })}
      </ListStyled>
    </StatsCardStyled>
  );
};
export default Statistics;
