import styled from "styled-components";

export const Avatar = styled.img`
  width: 150px;
  height: auto;
  border-radius: 50%;
  margin-left: auto;
  margin-right: auto;
  margin-bottom: 50px;
`;

// export const UserInfo = styled.div`
//   display: flex;
//   align-items: center;
// `;

export const UserName = styled.h2`
  margin-bottom: 10px;
  text-align: center;
`;

const styles = `margin-bottom: 10px;
  text-align: center;`;

export const Location = styled.p`
  ${styles}
`;

export const Tag = styled(Location);
