import styled from "styled-components";

export const StyledContainer = styled.div`
  /* text-align: center; */
  background-color: #282c34;
  min-height: 100vh;
  /* display: flex;
  flex-direction: column;
  align-items: center; */
  /* justify-content: center; */
  font-size: calc(10px + 2vmin);
  color: white;
`;

export const StyledCHeader = styled.header`
  height: 50px;
  width: max;
  padding: 10px;
  margin-bottom: 10px;

  box-shadow: 0px 5px 10px white;
`;

export const StyledCFooter = styled.footer`
  height: 50px;
  width: max;
  padding: 10px;
  margin-top: 10px;

  box-shadow: 0px -5px 10px white;
`;

export const StyledCSection = styled.section`
  width: 800px;
  margin-left: auto;
  margin-right: auto;
  padding: 15px;

  outline: 1px solid yellow;
`;
