import styled from "styled-components";

export const ListStyled = styled.ul`
  display: flex;
  /* justify-content: space-between; */

  width: 100%;
`;

export const ItemStyled = styled.li`
  flex-grow: 1;
  max-width: 33.33%;

  border: 2px solid white;

  font-size: 15px;
  text-align: center;
`;
