//   tr :first-child {
//     border-radius: 20px 0 0 0;
//   }

//   tr :last-child {
//     border-radius: 0 20px 0 0;
//   }
// `;

export const TrStyled = styled.tr`
  height: 20px;
`;

export const TbodyStyled = styled.tbody`
  tr :nth-child(2) {
    border-right: 1px solid #505050;
    border-left: 1px solid #505050;
  }

  tr:nth-child(2n + 1) {
    background-color: #ababab;
  }
`;
