import React from "react";

import {
  StatsCardStyled,
  ListStyled,
  TitleStyled,
  ItemStyled,
} from "components/Statistics/Statistics.styled";

const Statistics = ({ statistics, title }) => {
  const { id, label, percentage } = statistics;

  // const backgroundColor =

  const color = statistics.map((i) => {
    switch (i.label) {
      case ".docx":
        // console.log(".docx");
        return "#c12f2f";
        break;

      // case ".pdf":
      //   // console.log(".pdf");
      //   return "#538f29";

      // case ".mp3":
      //   // console.log(".mp3");
      //   return "#2f73c1";

      // case ".psd":
      //   // console.log(".psd");
      //   return "#cdcd13";

      // case ".mp4":
      //   // console.log(".mp4");
      //   return "#c12fc1";

      default:
        break;
    }
  });

  // console.log(color);

  return (
    <StatsCardStyled>
      {title && <TitleStyled>{title.toUpperCase()}</TitleStyled>}
      <ListStyled>
        {statistics.map((i) => {
          // console.log(i.label);
          return (
            <ItemStyled key={i.id} color={color}>
              <p>{i.label}</p>
              <p>{i.percentage}%</p>
            </ItemStyled>
          );
        })}
      </ListStyled>
    </StatsCardStyled>
  );
};
export default Statistics;
