import styled from "styled-components";

export const StatsCardStyled = styled.div`
  width: 300px;
  /* height: 500px; */
  /* padding: 10px; */
  padding-top: 20px;
  margin-top: 20px;
  margin-left: auto;
  margin-right: auto;
  border: 2px solid white;

  border-radius: 20px;
  box-shadow: 0px -5px 10px white;
`;

export const ListStyled = styled.ul`
  display: flex;
  flex-grow:1

  margin-top: 20px;
`;

export const TitleStyled = styled.h2`
  text-align: center;

  font-size: 20px;
`;
