import styled from "styled-components";

export const ListStyled = styled.ul`
  display: flex;
  justify-content: center;

  max-width: 100%;
`;

export const ItemStyled = styled.li`
  display: flex;
  flex-grow: 1;

  border: 2px solid white;

  font-size: 15px;
`;
